<?php

namespace Capco\AppBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * ProposalFormRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProposalFormRepository extends EntityRepository
{
    /**
     * @param $id
     *
     * @throws \Doctrine\ORM\NonUniqueResultException
     *
     * @return mixed
     */
    public function getOne($id)
    {
        $qb = $this->createQueryBuilder('pf')
            ->addSelect('pf', 'p', 'q')
            ->leftJoin('pf.proposals', 'p')
            ->leftJoin('pf.questions', 'q')
            ->andWhere('pf.id = :id')
            ->setParameter('id', $id)
        ;

        return $qb->getQuery()->getOneOrNullResult();
    }

    public function getLastProposalReference(string $formId)
    {
        $qb = $this->createQueryBuilder('f')
            ->select('MAX(p.reference) AS last_reference')
            ->leftJoin('f.proposals', 'p')
            ->where('f.id = :form_id')
            ->setParameter('form_id', $formId);

        return $qb->getQuery()->getSQL() ?? 0;
    }
}
