<?php

namespace Capco\AppBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * ResponseRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ResponseRepository extends EntityRepository
{
    public function getByReplyAsArray($replyId)
    {
        $qb = $this->createQueryBuilder('r')
            ->addSelect('question')
            ->leftJoin('r.question', 'question')
            ->andWhere('r.reply = :reply')
            ->setParameter('reply', $replyId)
        ;

        return $qb->getQuery()->getArrayResult();
    }

    public function getByProposal(int $proposalId, bool $showPrivate = false)
    {
        $qb = $this->createQueryBuilder('r')
            ->addSelect('question')
            ->leftJoin('r.question', 'question')
            ->andWhere('r.proposal = :proposal')
            ->setParameter('proposal', $proposalId)
        ;

        if (!$showPrivate) {
            $qb
                ->andWhere('question.private = false')
            ;
        }

        return $qb->getQuery()->getResult();
    }
}
